import java.util.Scanner;public class TEST {     static double method(double arg,int dokl){        double res=arg;         double rezultat=0, wynik=0;        if(dokl==1)            return arg;        wynik=Math.pow(-1,((dokl+1)/2)+1) * Math.abs(method(arg,dokl-2)) * Math.pow(arg,2)/((dokl-1)*dokl);        rezultat+=wynik;         System.out.println(((dokl+1)/2)+1);        System.out.println("Result:"+wynik);         return wynik;    }    public static void main(String[] args) {        Scanner scanner= new Scanner(System.in);        int dokl= scanner.nextInt();        dokl= (dokl*2)-1;        System.out.println(dokl);        System.out.println(method(10,dokl));    }}