import java.math.BigDecimal;import java.math.MathContext;import java.math.RoundingMode;import java.util.Scanner;public class Stepene {    public static void main(String[] args) {        System.out.println(new BigDecimal(Math.sin(10)));        System.out.println(new BigDecimal(-0.5440211108897564));        BigDecimal o1= new BigDecimal(Math.sin(10));        BigDecimal o2= new BigDecimal(-0.5440211108897564);        BigDecimal result= new BigDecimal(String.valueOf(o1.subtract(o2,new MathContext(4, RoundingMode.HALF_UP))));        System.out.println("==========");        System.out.println(result);        System.out.println(o1.subtract(o2));        System.out.println("===========");        double numrad = 10 * (2 * Math.PI/360);        System.out.println(numrad);        System.out.println(Math.toRadians(10));    }}